import React, { useState } from "react";
import axios from "axios";

function Admindashboard() {
  const [activeMenu, setActiveMenu] = useState("add");
  const [events, setEvents] = useState([]);
  const [users, setUsers] = useState([]);

  const [editId, setEditId] = useState(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [location, setLocation] = useState("");

  /* ========== ADD / EDIT EVENT ========== */

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = { title, description, date, time, location };

    try {
      if (editId) {
        await axios.put(`http://localhost:5000/event/editevent/${editId}`, data);
        alert("Event updated");
      } else {
        await axios.post("http://localhost:5000/event/addevent", data);
        alert("Event added");
      }
      clearForm();
      fetchEvents();
      setActiveMenu("events");
    } catch {
      alert("Error saving event");
    }
  };

  const clearForm = () => {
    setTitle("");
    setDescription("");
    setDate("");
    setTime("");
    setLocation("");
    setEditId(null);
  };

  /* ========== EVENTS ========== */

  const fetchEvents = async () => {
    const res = await axios.get("http://localhost:5000/event/allevents");
    setEvents(res.data);
  };

  const deleteEvent = async (id) => {
    if (!window.confirm("Delete this event?")) return;
    await axios.delete(`http://localhost:5000/event/deleteevent/${id}`);
    fetchEvents();
  };

  const editEvent = (ev) => {
    setTitle(ev.title);
    setDescription(ev.description);
    setDate(ev.date);
    setTime(ev.time);
    setLocation(ev.location);
    setEditId(ev._id);
    setActiveMenu("add");
  };

  const viewUsers = async (eventId) => {
    const res = await axios.get(
      `http://localhost:5000/event/registrations/${eventId}`
    );
    setUsers(res.data);
    setActiveMenu("users");
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* SIDEBAR */}
      <div className="w-48 bg-black text-white p-4">
        <h2 className="text-lg font-semibold mb-4">Admin</h2>

        <button
          className="block w-full text-left mb-2 hover:text-gray-300"
          onClick={() => { clearForm(); setActiveMenu("add"); }}
        >
          Add Event
        </button>

        <button
          className="block w-full text-left mb-2 hover:text-gray-300"
          onClick={() => { fetchEvents(); setActiveMenu("events"); }}
        >
          Manage Events
        </button>
      </div>

      {/* MAIN CONTENT */}
      <div className="flex-1 p-6">
        {activeMenu === "add" && (
          <form
            onSubmit={handleSubmit}
            className="bg-white p-4 rounded shadow max-w-md"
          >
            <h3 className="font-semibold mb-3">
              {editId ? "Edit Event" : "Add Event"}
            </h3>

            <input className="border p-2 w-full mb-2" placeholder="Title" value={title} onChange={e=>setTitle(e.target.value)} />
            <textarea className="border p-2 w-full mb-2" placeholder="Description" value={description} onChange={e=>setDescription(e.target.value)} />
            <input className="border p-2 w-full mb-2" type="date" value={date} onChange={e=>setDate(e.target.value)} />
            <input className="border p-2 w-full mb-2" type="time" value={time} onChange={e=>setTime(e.target.value)} />
            <input className="border p-2 w-full mb-3" placeholder="Location" value={location} onChange={e=>setLocation(e.target.value)} />

            <button className="bg-blue-600 text-white px-4 py-2 rounded">
              {editId ? "Update" : "Save"}
            </button>
          </form>
        )}

        {activeMenu === "events" && (
          <div className="max-w-md">
            {events.map(ev => (
              <div key={ev._id} className="bg-white p-3 rounded shadow mb-3">
                <h4 className="font-semibold">{ev.title}</h4>
                <p className="text-sm">{ev.date} | {ev.time}</p>
                <div className="flex gap-2 mt-2">
                  <button className="bg-yellow-500 text-white px-2 py-1 text-sm rounded" onClick={()=>editEvent(ev)}>Edit</button>
                  <button className="bg-red-600 text-white px-2 py-1 text-sm rounded" onClick={()=>deleteEvent(ev._id)}>Delete</button>
                  <button className="bg-green-600 text-white px-2 py-1 text-sm rounded" onClick={()=>viewUsers(ev._id)}>Users</button>
                </div>
              </div>
            ))}
          </div>
        )}

        {activeMenu === "users" && (
          <div className="bg-white p-4 rounded shadow max-w-md">
            <h3 className="font-semibold mb-2">Registered Users</h3>
            {users.length === 0 ? <p>No registrations</p> :
              users.map(u => (
                <p key={u._id} className="text-sm">
                  {u.userId.name} â€“ {u.userId.email}
                </p>
              ))
            }
          </div>
        )}
      </div>
    </div>
  );
}

export default Admindashboard;
