import React, { useState } from "react";
import axios from "axios";

function Admindashboard() {
  const [showForm, setShowForm] = useState(false);
  const [showEvents, setShowEvents] = useState(false);
  const [events, setEvents] = useState([]);

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [location, setLocation] = useState("");

  // ADD EVENT
  const handleSubmit = async (e) => {
    e.preventDefault();

    const eventData = { title, description, date, time, location };

    try {
      const res = await axios.post(
        "http://localhost:5000/event/addevent",
        eventData
      );

      alert(res.data.message);

      setTitle("");
      setDescription("");
      setDate("");
      setTime("");
      setLocation("");
      setShowForm(false);
      setShowEvents(false);
    } catch (error) {
      console.log(error);
      alert("Error adding event");
    }
  };

  // VIEW EVENTS
  const fetchEvents = async () => {
    try {
      const res = await axios.get("http://localhost:5000/event/allevents");
      setEvents(res.data);
      setShowEvents(true)
      setShowForm(false)
     
    } catch (error) {
      alert("Error fetching events");
    }
  };

  return (
    <div style={pageStyle}>
      {/* TOP BUTTONS */}
      <div style={{ marginBottom: "20px" }}>
        <button style={topButton} onClick={() => {setShowForm(true); setShowEvents(false)}}>
          Add Event
        </button>
        <button style={topButton} onClick={fetchEvents}>
          View Events
        </button>
      </div>

      {/* ADD EVENT FORM */}
      {showForm && (
        <form style={formStyle} onSubmit={handleSubmit}>
          <span style={closeStyle} onClick={() => setShowForm(false)}>
            Ã—
          </span>

          <h3>Add Event</h3>

          <input
            type="text"
            placeholder="Event Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            style={inputStyle}
            required
          />

          <textarea
            placeholder="Event Description"
            rows="3"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            style={inputStyle}
            required
          />

          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            style={inputStyle}
            required
          />

          <input
            type="time"
            step="60"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            style={inputStyle}
            required
          />

          <input
            type="text"
            placeholder="Event Location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            style={inputStyle}
            required
          />

          <button type="submit" style={submitButton}>
            Save Event
          </button>
        </form>
      )}

      {/* VIEW ALL EVENTS */}
      {showEvents && (
        <div style={listStyle}>
          <h3>All Events</h3>

          {events.length === 0 ? (
            <p>No events found</p>
          ) : (
            events.map((ev) => (
              <div key={ev._id} style={eventCard}>
                <h4>{ev.title}</h4>
                <p>{ev.description}</p>
                <p>
                  {ev.date} | {ev.time}
                </p>
                <p>{ev.location}</p>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}

/* ================= STYLES ================= */

const pageStyle = {
  minHeight: "100vh",
  background: "#f5f5f5",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  paddingTop: "20px"
};

const topButton = {
  padding: "8px 14px",
  marginRight: "10px",
  background: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer"
};

const formStyle = {
  width: "350px",
  background: "#fff",
  padding: "15px",
  borderRadius: "6px",
  position: "relative"
};

const closeStyle = {
  position: "absolute",
  top: "8px",
  right: "10px",
  cursor: "pointer",
  fontSize: "18px",
  fontWeight: "bold"
};

const inputStyle = {
  width: "100%",
  padding: "7px",
  marginBottom: "10px",
  border: "1px solid #ccc"
};

const submitButton = {
  width: "100%",
  padding: "8px",
  background: "#007bff",
  color: "#fff",
  border: "none",
  cursor: "pointer"
};

const listStyle = {
  width: "350px",
  marginTop: "20px"
};

const eventCard = {
  background: "#fff",
  padding: "10px",
  marginBottom: "10px",
  borderRadius: "5px"
};

export default Admindashboard;
