import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [events, setEvents] = useState([]);
  const [message, setMessage] = useState("");
  const [type, setType] = useState("");

  const token = localStorage.getItem("token");
  const navigate = useNavigate();

  useEffect(() => {
    fetchEvents();
  }, []);

  const showMessage = (msg, msgType = "error") => {
    setMessage(msg);
    setType(msgType);
    setTimeout(() => setMessage(""), 2500);
  };

  const fetchEvents = async () => {
    try {
      const res = await axios.get("http://localhost:5000/event/allevents");
      setEvents(res.data);
    } catch {
      showMessage("Unable to load events");
    }
  };

  const registerEvent = async (eventId) => {
    try {
      const res = await axios.post(
        "http://localhost:5000/event/registerevent",
        { eventId },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      showMessage(res.data.message, "success");
    } catch {
      showMessage("Registration failed");
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* TOP BAR */}
      <div className="bg-white border-b">
        <div className="max-w-6xl mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="text-xl font-semibold text-gray-800">
            Event Portal
          </h1>
          <button
            onClick={logout}
            className="text-sm text-red-600 hover:underline"
          >
            Logout
          </button>
        </div>
      </div>

      {/* CONTENT */}
      <div className="max-w-6xl mx-auto px-6 py-8">
        <div className="mb-6">
          <h2 className="text-2xl font-semibold text-gray-800">
            Upcoming Events
          </h2>
          <p className="text-sm text-gray-500">
            Browse and register for available events
          </p>
        </div>

        {/* MESSAGE */}
        {message && (
          <div
            className={`mb-5 px-4 py-2 rounded text-sm w-fit ${
              type === "success"
                ? "bg-green-100 text-green-700"
                : "bg-red-100 text-red-700"
            }`}
          >
            {message}
          </div>
        )}

        {/* EVENTS GRID */}
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {events.map((event) => (
            <div
              key={event._id}
              className="bg-white rounded-lg border p-5 hover:shadow transition"
            >
              <h3 className="text-lg font-semibold text-gray-800 mb-1">
                {event.title}
              </h3>

              <p className="text-sm text-gray-600 mb-3 line-clamp-2">
                {event.description}
              </p>

              <div className="text-sm text-gray-500 space-y-1 mb-4">
                <p>üìÖ {event.date}</p>
                <p>‚è∞ {event.time}</p>
                <p>üìç {event.location}</p>
              </div>

              <button
                onClick={() => registerEvent(event._id)}
                className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
              >
                Register
              </button>
            </div>
          ))}
        </div>

        {events.length === 0 && (
          <p className="text-center text-gray-500 mt-12">
            No events available right now
          </p>
        )}
      </div>
    </div>
  );
};

export default Home;
