import React, { useState } from "react";
import axios from "axios";

function Admindashboard() {
  const [showForm, setShowForm] = useState(false);
  const [showEvents, setShowEvents] = useState(false);
  const [showUsers, setShowUsers] = useState(false);

  const [events, setEvents] = useState([]);
  const [users, setUsers] = useState([]);

  const [editId, setEditId] = useState(null);

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [location, setLocation] = useState("");

  /* ================= ADD / UPDATE EVENT ================= */

  const handleSubmit = async (e) => {
    e.preventDefault();

    const eventData = { title, description, date, time, location };

    try {
      if (editId) {
        await axios.put(
          `http://localhost:5000/event/editevent/${editId}`,
          eventData
        );
        alert("Event updated successfully");
      } else {
        const res = await axios.post(
          "http://localhost:5000/event/addevent",
          eventData
        );
        alert(res.data.message);
      }

      clearForm();
      fetchEvents();
      setShowForm(false);
      setShowEvents(true);
    } catch (error) {
      alert("Error saving event");
    }
  };

  const clearForm = () => {
    setTitle("");
    setDescription("");
    setDate("");
    setTime("");
    setLocation("");
    setEditId(null);
  };

  /* ================= VIEW EVENTS ================= */

  const fetchEvents = async () => {
    try {
      const res = await axios.get("http://localhost:5000/event/allevents");
      setEvents(res.data);
      setShowEvents(true);
      setShowForm(false);
      setShowUsers(false);
    } catch (error) {
      alert("Error fetching events");
    }
  };

  /* ================= DELETE EVENT ================= */

  const deleteEvent = async (id) => {
    if (!window.confirm("Delete this event?")) return;

    try {
      await axios.delete(`http://localhost:5000/event/deleteevent/${id}`);
      fetchEvents();
    } catch (error) {
      alert("Delete failed");
    }
  };

  /* ================= EDIT EVENT ================= */

  const editEvent = (ev) => {
    setTitle(ev.title);
    setDescription(ev.description);
    setDate(ev.date);
    setTime(ev.time);
    setLocation(ev.location);
    setEditId(ev._id);

    setShowForm(true);
    setShowEvents(false);
    setShowUsers(false);
  };

  /* ================= VIEW REGISTERED USERS ================= */

  const viewUsers = async (eventId) => {
    try {
      const res = await axios.get(
        `http://localhost:5000/event/registrations/${eventId}`
      );
      setUsers(res.data);
      setShowUsers(true);
      setShowForm(false);
    } catch (error) {
      alert("Error fetching users");
    }
  };

  return (
    <div style={pageStyle}>
      <h2>Admin Dashboard</h2>

      {/* TOP BUTTONS */}
      <div style={{ marginBottom: "15px" }}>
        <button style={topButton} onClick={() => { clearForm(); setShowForm(true); }}>
          Add Event
        </button>
        <button style={topButton} onClick={fetchEvents}>
          View Events
        </button>
      </div>

      {/* ADD / EDIT EVENT FORM */}
      {showForm && (
        <form style={formStyle} onSubmit={handleSubmit}>
          <h3>{editId ? "Edit Event" : "Add Event"}</h3>

          <input
            type="text"
            placeholder="Event Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            style={inputStyle}
            required
          />

          <textarea
            placeholder="Event Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            style={inputStyle}
            required
          />

          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            style={inputStyle}
            required
          />

          <input
            type="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            style={inputStyle}
            required
          />

          <input
            type="text"
            placeholder="Location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            style={inputStyle}
            required
          />

          <button type="submit" style={submitButton}>
            {editId ? "Update Event" : "Save Event"}
          </button>
        </form>
      )}

      {/* VIEW EVENTS */}
      {showEvents && (
        <div style={listStyle}>
          <h3>All Events</h3>

          {events.map((ev) => (
            <div key={ev._id} style={eventCard}>
              <h4>{ev.title}</h4>
              <p>{ev.description}</p>
              <p>{ev.date} | {ev.time}</p>
              <p>{ev.location}</p>

              <button style={editBtn} onClick={() => editEvent(ev)}>Edit</button>
              <button style={deleteBtn} onClick={() => deleteEvent(ev._id)}>Delete</button>
              <button style={userBtn} onClick={() => viewUsers(ev._id)}>Users</button>
            </div>
          ))}
        </div>
      )}

      {/* VIEW REGISTERED USERS */}
      {showUsers && (
        <div style={listStyle}>
          <h3>Registered Users</h3>
          {users.length === 0 ? (
            <p>No registrations</p>
          ) : (
            users.map((u) => (
              <p key={u._id}>
                {u.userId.name} â€“ {u.userId.email}
              </p>
            ))
          )}
        </div>
      )}
    </div>
  );
}

/* ================= STYLES ================= */

const pageStyle = {
  minHeight: "100vh",
  background: "#f5f5f5",
  padding: "20px",
  textAlign: "center"
};

const topButton = {
  padding: "8px 14px",
  marginRight: "10px",
  background: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer"
};

const formStyle = {
  width: "350px",
  background: "#fff",
  padding: "15px",
  margin: "auto",
  marginBottom: "20px"
};

const inputStyle = {
  width: "100%",
  padding: "7px",
  marginBottom: "10px",
  border: "1px solid #ccc"
};

const submitButton = {
  width: "100%",
  padding: "8px",
  background: "#007bff",
  color: "#fff",
  border: "none",
  cursor: "pointer"
};

const listStyle = {
  width: "350px",
  margin: "auto"
};

const eventCard = {
  background: "#fff",
  padding: "10px",
  marginBottom: "10px"
};

const editBtn = {
  marginRight: "5px",
  padding: "5px",
  background: "orange",
  color: "#fff",
  border: "none"
};

const deleteBtn = {
  marginRight: "5px",
  padding: "5px",
  background: "red",
  color: "#fff",
  border: "none"
};

const userBtn = {
  padding: "5px",
  background: "green",
  color: "#fff",
  border: "none"
};

export default Admindashboard;
