import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  // alert state
  const [message, setMessage] = useState("");
  const [type, setType] = useState(""); // success | error

  const nav = useNavigate();

  const showMessage = (msg, msgType = "error") => {
    setMessage(msg);
    setType(msgType);

    // auto hide after 2.5 sec
    setTimeout(() => {
      setMessage("");
    }, 2500);
  };

  const handlelogin = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      showMessage("Please enter email and password");
      return;
    }

    try {
      setLoading(true);
      const res = await axios.post(
        "http://localhost:5000/register/login",
        { email, password }
      );

      if (res.data.success) {
        showMessage("Login successful", "success");
        localStorage.setItem("token", res.data.token);

        setTimeout(() => {
          if (res.data.role === "admin") {
            nav("/admin");
          } else {
            nav("/home");
          }
        }, 1000);
      } else {
        showMessage(res.data.message || "Login failed");
      }
    } catch (error) {
      showMessage("Something went wrong");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 px-4">
      <div className="bg-white p-6 rounded shadow-md w-full max-w-sm">
        <h2 className="text-2xl font-semibold text-center mb-1">
          Welcome Back
        </h2>
        <p className="text-sm text-gray-500 text-center mb-4">
          Login to continue
        </p>

        {/* MESSAGE BOX */}
        {message && (
          <div
            className={`mb-3 text-sm px-3 py-2 rounded ${
              type === "success"
                ? "bg-green-100 text-green-700"
                : "bg-red-100 text-red-700"
            }`}
          >
            {message}
          </div>
        )}

        <form onSubmit={handlelogin} className="space-y-3">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full border p-2 rounded focus:outline-none focus:ring-1 focus:ring-blue-500"
          />

          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full border p-2 rounded focus:outline-none focus:ring-1 focus:ring-blue-500"
          />

          <button
            type="submit"
            disabled={loading}
            className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
          >
            {loading ? "Logging in..." : "Login"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
