import React, { useState } from "react";
import axios from "axios"
import { useNavigate } from "react-router-dom";
const Register = () => {

    const [name,setName]=useState("")
    const [email,setEmail]=useState("")
    const [password,setPassword]=useState("")
    const [role,setRole]=useState("user")
const nav = useNavigate()
const handlesubmit=async(e)=>{
    e.preventDefault()
    const regdata = {name,email,password,role}
    try {
        const res = await axios.post("http://localhost:5000/register/addregister",regdata)
        alert(res.data.message)
        setName("")
        setEmail("")
        setPassword("")
        nav("/login")
    } catch (error) {
        alert("error")
    }
}

    return (
    <div style={styles.container}>
<form onSubmit={handlesubmit}>    
     <h2 style={styles.title}>Register</h2>

      <input type="text" placeholder="Name" value={name} onChange={(e)=>setName(e.target.value)} style={styles.input} />
      <input type="email" placeholder="Email" value={email} onChange={(e)=>setEmail(e.target.value)}style={styles.input} />
      <input type="password" placeholder="Password"value={password} onChange={(e)=>setPassword(e.target.value)} style={styles.input} />

      <button type="submit" style={styles.button}>Register</button>
      </form> 
    </div>
  );
};


const styles = {
  container: {
    width: "300px",
    margin: "100px auto",
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "5px",
    textAlign: "center",
  },
  title: {
    marginBottom: "20px",
    color:"blue"

  },
  input: {
    width: "100%",
    padding: "8px",
    marginBottom: "10px",
    marginLeft:"-10px"
  },
  button: {
    width: "100%",
    padding: "8px",
    backgroundColor: "blue",
    color: "white",
    border: "none",
    cursor: "pointer",
  },
};

export default Register;
