import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [events, setEvents] = useState([]);
  const [message, setMessage] = useState("");
  const [type, setType] = useState(""); // success | error

  const token = localStorage.getItem("token");
  const navigate = useNavigate();

  useEffect(() => {
    fetchEvents();
  }, []);

  const showMessage = (msg, msgType = "error") => {
    setMessage(msg);
    setType(msgType);

    setTimeout(() => {
      setMessage("");
    }, 2500);
  };

  const fetchEvents = async () => {
    try {
      const res = await axios.get("http://localhost:5000/event/allevents");
      setEvents(res.data);
    } catch (error) {
      showMessage("Failed to load events");
    }
  };

  const registerEvent = async (eventId) => {
    try {
      const res = await axios.post(
        "http://localhost:5000/event/registerevent",
        { eventId },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      showMessage(res.data.message, "success");
    } catch (error) {
      showMessage("Registration failed");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* HEADER */}
      <div className="flex justify-between items-center bg-white px-6 py-4 shadow">
        <h1 className="text-xl font-semibold">Upcoming Events</h1>

        <button
          onClick={handleLogout}
          className="text-sm bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
        >
          Logout
        </button>
      </div>

      <div className="p-6">
        {/* MESSAGE BOX */}
        {message && (
          <div
            className={`mb-4 px-4 py-2 rounded text-sm ${
              type === "success"
                ? "bg-green-100 text-green-700"
                : "bg-red-100 text-red-700"
            }`}
          >
            {message}
          </div>
        )}

        {/* EVENTS */}
        <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
          {events.map((event) => (
            <div
              key={event._id}
              className="bg-white p-5 rounded shadow"
            >
              <h2 className="text-lg font-semibold mb-1">
                {event.title}
              </h2>
              <p className="text-gray-600 text-sm mb-2">
                {event.description}
              </p>
              <p className="text-sm">
                üìÖ {event.date} | ‚è∞ {event.time}
              </p>
              <p className="text-sm mb-4">
                üìç {event.location}
              </p>

              <button
                onClick={() => registerEvent(event._id)}
                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
              >
                Register
              </button>
            </div>
          ))}
        </div>

        {events.length === 0 && (
          <p className="text-center text-gray-500 mt-10">
            No events available
          </p>
        )}
      </div>
    </div>
  );
};

export default Home;
