import React, { useState } from "react";
import axios from "axios";

function Admindashboard() {
  const [showForm, setShowForm] = useState(false);
  const [showEvents, setShowEvents] = useState(false);
  const [showUsers, setShowUsers] = useState(false);

  const [events, setEvents] = useState([]);
  const [users, setUsers] = useState([]);

  const [editId, setEditId] = useState(null);

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [location, setLocation] = useState("");

  /* ================= ADD / UPDATE EVENT ================= */

  const handleSubmit = async (e) => {
    e.preventDefault();

    const eventData = { title, description, date, time, location };

    try {
      if (editId) {
        await axios.put(
          `http://localhost:5000/event/editevent/${editId}`,
          eventData
        );
        alert("Event updated successfully");
      } else {
        const res = await axios.post(
          "http://localhost:5000/event/addevent",
          eventData
        );
        alert(res.data.message);
      }

      clearForm();
      fetchEvents();
      setShowForm(false);
      setShowEvents(true);
    } catch (error) {
      alert("Error saving event");
    }
  };

  const clearForm = () => {
    setTitle("");
    setDescription("");
    setDate("");
    setTime("");
    setLocation("");
    setEditId(null);
  };

  /* ================= VIEW EVENTS ================= */

  const fetchEvents = async () => {
    try {
      const res = await axios.get("http://localhost:5000/event/allevents");
      setEvents(res.data);
      setShowEvents(true);
      setShowForm(false);
      setShowUsers(false);
    } catch (error) {
      alert("Error fetching events");
    }
  };

  /* ================= DELETE EVENT ================= */

  const deleteEvent = async (id) => {
    if (!window.confirm("Delete this event?")) return;

    try {
      await axios.delete(`http://localhost:5000/event/deleteevent/${id}`);
      fetchEvents();
    } catch (error) {
      alert("Delete failed");
    }
  };

  /* ================= EDIT EVENT ================= */

  const editEvent = (ev) => {
    setTitle(ev.title);
    setDescription(ev.description);
    setDate(ev.date);
    setTime(ev.time);
    setLocation(ev.location);
    setEditId(ev._id);

    setShowForm(true);
    setShowEvents(false);
    setShowUsers(false);
  };

  /* ================= VIEW REGISTERED USERS ================= */

  const viewUsers = async (eventId) => {
    try {
      const res = await axios.get(
        `http://localhost:5000/event/registrations/${eventId}`
      );
      setUsers(res.data);
      setShowUsers(true);
      setShowForm(false);
    } catch (error) {
      alert("Error fetching users");
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h2 className="text-2xl font-semibold text-center mb-6">
        Admin Dashboard
      </h2>

      {/* TOP BUTTONS */}
      <div className="flex justify-center gap-3 mb-6">
        <button
          className="bg-black text-white px-4 py-2 rounded"
          onClick={() => {
            clearForm();
            setShowForm(true);
            setShowEvents(false);
            setShowUsers(false);
          }}
        >
          Add Event
        </button>

        <button
          className="bg-black text-white px-4 py-2 rounded"
          onClick={fetchEvents}
        >
          View Events
        </button>
      </div>

      {/* ADD / EDIT EVENT FORM */}
      {showForm && (
        <form
          onSubmit={handleSubmit}
          className="bg-white p-4 rounded shadow w-full max-w-sm mx-auto mb-6"
        >
          <h3 className="text-lg font-semibold mb-3">
            {editId ? "Edit Event" : "Add Event"}
          </h3>

          <input
            type="text"
            placeholder="Event Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="w-full border p-2 mb-2"
            required
          />

          <textarea
            placeholder="Event Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full border p-2 mb-2"
            required
          />

          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full border p-2 mb-2"
            required
          />

          <input
            type="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            className="w-full border p-2 mb-2"
            required
          />

          <input
            type="text"
            placeholder="Location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            className="w-full border p-2 mb-3"
            required
          />

          <button className="w-full bg-blue-600 text-white py-2 rounded">
            {editId ? "Update Event" : "Save Event"}
          </button>
        </form>
      )}

      {/* VIEW EVENTS */}
      {showEvents && (
        <div className="w-full max-w-sm mx-auto">
          <h3 className="text-lg font-semibold mb-3">All Events</h3>

          {events.map((ev) => (
            <div
              key={ev._id}
              className="bg-white p-3 rounded shadow mb-3"
            >
              <h4 className="font-semibold">{ev.title}</h4>
              <p className="text-sm">{ev.description}</p>
              <p className="text-sm">
                {ev.date} | {ev.time}
              </p>
              <p className="text-sm mb-2">{ev.location}</p>

              <div className="flex gap-2">
                <button
                  className="bg-yellow-500 text-white px-2 py-1 rounded text-sm"
                  onClick={() => editEvent(ev)}
                >
                  Edit
                </button>
                <button
                  className="bg-red-600 text-white px-2 py-1 rounded text-sm"
                  onClick={() => deleteEvent(ev._id)}
                >
                  Delete
                </button>
                <button
                  className="bg-green-600 text-white px-2 py-1 rounded text-sm"
                  onClick={() => viewUsers(ev._id)}
                >
                  Users
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* VIEW REGISTERED USERS */}
      {showUsers && (
        <div className="w-full max-w-sm mx-auto bg-white p-4 rounded shadow">
          <h3 className="text-lg font-semibold mb-2">Registered Users</h3>

          {users.length === 0 ? (
            <p>No registrations</p>
          ) : (
            users.map((u) => (
              <p key={u._id} className="text-sm">
                {u.userId.name} â€“ {u.userId.email}
              </p>
            ))
          )}
        </div>
      )}
    </div>
  );
}

export default Admindashboard;
